	draw(interp(C,D,n/10)--interp(C,B,n/10),red);
}

for (n = 1; n <= 19; ++n) {
  if (n % 2 == 1) {
	  draw(interp(D,C,n/20)--interp(D,A,n/20),blue);
		draw(interp(B,C,n/20)--interp(B,A,n/20),blue);
